import React, { useState, useEffect } from 'react';
import { Container, Card, ListGroup, Spinner, Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import washrooms from './washrooms.json';
import { calculateDistance } from './utils/distanceCalculator';
import StarRating from './components/StarRating';
import HumanRating from './components/HumanRating';

function App() {
  // ... [Keep all the state and effect hooks from previous version] ...

  return (
    <Container className="app-container">
      <h1 className="title-text text-center mb-5 display-4">
        üöª Washroom Finder
      </h1>

      {userLocation && (
        <Card className="location-card mb-5">
          <Card.Body className="text-center py-3">
            <span className="location-text">
              üìç Your Location: 
              <span className="text-primary ms-2">
                {userLocation.lat.toFixed(4)}, {userLocation.lng.toFixed(4)}
              </span>
            </span>
          </Card.Body>
        </Card>
      )}

      {nearbyWashrooms.length === 0 ? (
        <Card className="no-results-card text-center p-4 border-0">
          <span className="h4 text-muted">üö´ No washrooms nearby</span>
          <div className="mt-3">‚ú® Try moving around!</div>
        </Card>
      ) : (
        <div className="washrooms-list">
          {nearbyWashrooms.map((washroom, index) => {
            const starAvg = washroom.starRatings.length > 0 
              ? washroom.starRatings.reduce((a, b) => a + b, 0) / washroom.starRatings.length
              : 0;

            const humanAvg = washroom.humanRatings.length > 0 
              ? washroom.humanRatings.reduce((a, b) => a + b, 0) / washroom.humanRatings.length
              : 0;

            return (
              <Card 
                key={washroom.id}
                className="washroom-card mb-4"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <Card.Body className="p-4">
                  <div className="d-flex justify-content-between align-items-start">
                    <div>
                      <Card.Title className="h3 mb-3 text-primary">
                        üöΩ {washroom.name}
                      </Card.Title>
                      <Card.Text className="text-muted mb-4">
                        üìç {washroom.address}
                      </Card.Text>
                    </div>
                    <span className="distance-badge">
                      {calculateDistance(
                        userLocation?.lat,
                        userLocation?.lng,
                        washroom.lat,
                        washroom.lng
                      ).toFixed(2)} km
                    </span>
                  </div>

                  <div className="rating-group">
                    <StarRating 
                      averageRating={starAvg}
                      onRate={(rating) => handleRating(washroom.id, 'starRatings', rating)}
                    />
                    <HumanRating 
                      averageRating={humanAvg}
                      onRate={(rating) => handleRating(washroom.id, 'humanRatings', rating)}
                    />
                  </div>
                </Card.Body>
              </Card>
            )
          })}
        </div>
      )}
    </Container>
  );
}

export default App;
